import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Login() {
  const history = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(''); // For showing the success message

  async function submit(e) {
    e.preventDefault();

    try {
      // Sending signup details to the backend
      await axios.post("http://localhost:8000/signup", {
        email,
        password
      })
        .then(res => {
          if (res.data === "exist") {
            alert("User already exists");
          } else if (res.data === "notexist") {
            setMessage("User successfully registered! Redirecting to login..."); // Set success message
            setTimeout(() => {
              history("/", { state: { id: email } }); // Redirect to login after 2 seconds
            }, 2000);
          }
        })
        .catch(e => {
          alert("Error occurred while registering.");
          console.log(e);
        });

    } catch (e) {
      console.log(e);
    }
  }

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gray-100 py-6">
      <h1 className="text-3xl font-semibold text-blue-600 mb-4">Signup</h1>

      {/* Show success message if the user is successfully registered */}
      {message && <p className="text-green-600 font-medium mb-4">{message}</p>}

      <form 
        onSubmit={submit} 
        className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 w-96"
      >
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
            Email
          </label>
          <input
            type="email"
            id="email"
            onChange={(e) => { setEmail(e.target.value) }}
            placeholder="Enter your email"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring focus:ring-blue-300"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
            Password
          </label>
          <input
            type="password"
            id="password"
            onChange={(e) => { setPassword(e.target.value) }}
            placeholder="Enter your password"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring focus:ring-blue-300"
            required
          />
        </div>

        <div className="flex items-center justify-between">
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Register
          </button>
        </div>
      </form>

      <p className="text-gray-500 mb-4">OR</p>

      <Link 
        to="/" 
        className="text-blue-500 hover:underline"
      >
        Login Page
      </Link>
    </div>
  );
}

export default Login;
